<template>
  <view>
    <!--  :options="options" @success="onSuccess" @scroll="onScroll" -->
    <list ref="list" :options="options" @success="onSuccess" @scroll="onScroll">
      <view
        class="item"
        v-for="(item, index) in list"
        :key="index"
        @click="goDetail(item)"
        >{{ item[name] }} <img class="inner_img" src="/static/list/right.png"
      /></view>

      <!-- <view class="scroll-top" @click="$refs.list.scrollTop()" v-show="showToTop">回到顶部</view> -->
    </list>
  </view>
</template>

<script>
import List from "./list.js";
import NameList from "./name_list.js";

export default {
  data() {
    return {
      list: [],
      showToTop: false,
      list_data:"",
      
      // name_list:[["11","name"],["12","projectName"]],
      name:"",
    };
  },
  computed: {
    options() {
      var base_url
      if (this.list_data == "23") {
        base_url=List.List.get(this.list_data)
      }
      else{
        base_url=this.$api + List.List.get(this.list_data)
      }
      return {
        url: base_url,
        params: {},
        // this.$api + 
      };
    },
  },
  methods: {
    onSuccess(list) {
      this.list = list;
      console.log("请求成功，当前的列表内容为：", list);

      this.name=NameList.NameList.get(this.list_data)

    },
    onScroll(e) {
      const { scrollTop } = e.target;
      if (scrollTop > 100) {
        this.showToTop = true;
      } else {
        this.showToTop = false;
      }
    },
    goDetail: function (e) {
      let detail = {
        item: e,
        fun_id:this.list_data,
      };
      uni.navigateTo({
        url:
          "/pages/purchase/list/list?detailDate=" +JSON.stringify(detail),
        success: (res) => {},
        fail: () => {},
        complete: () => {},
      });
    },

    onLoad(options) {
      this.list_data= options.fun_id;
      // console.log(this.list_data);
      // console.log(List.List.get(this.list_data));
      // this.name=NameList.NameList.get(this.list_data)
      
    },
  },
  mounted() {
    this.$refs.list.request();
    // this.axios
    //   .get("news/api")
    //   .then((res) => {
    //     console.log(res);
    //   })
    //   .catch((err) => {
    //     console.log(err);
    //   });
  },
};
</script>
